import './App.css';
import { useState, useEffect } from 'react';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import ReactHtmlParser from 'react-html-parser';
import Axios from 'axios';

function Login(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  return (
    <>
      <h2>로그인</h2>

      <div className="form">
        <p><input className="login" type="text" name="username" placeholder="아이디" onChange={event => {
          setId(event.target.value);
        }} /></p>
        <p><input className="login" type="password" name="pwd" placeholder="비밀번호" onChange={event => {
          setPassword(event.target.value);
        }} /></p>

        <p><input className="btn" type="submit" value="로그인" onClick={() => {
          const userData = {
            userId: id,
            userPassword: password,
          };
          Axios.post("http://localhost:3001/login", userData)
            .then((res) => res.data)
            .then((json) => {            
              if(json.isLogin === "True"){
                props.setMode("WELCOME");
              }
              else {
                alert(json.isLogin);
              }
            });
        }} /></p>
      </div>

      <p>계정이 없으신가요?  <button onClick={() => {
        props.setMode("SIGNIN");
      }}>회원가입</button></p>
    </>
  );
}


function Signin(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  return (
    <>
      <h2>회원가입</h2>

      <div className="form">
        <p><input className="login" type="text" placeholder="아이디" onChange={event => {
          setId(event.target.value);
        }} /></p>
        <p><input className="login" type="password" placeholder="비밀번호" onChange={event => {
          setPassword(event.target.value);
        }} /></p>
        <p><input className="login" type="password" placeholder="비밀번호 확인" onChange={event => {
          setPassword2(event.target.value);
        }} /></p>

        <p><input className="btn" type="submit" value="회원가입" onClick={() => {
          const userData = {
            userId: id,
            userPassword: password,
            userPassword2: password2,
          };
          Axios.post("http://localhost:3001/signin", userData)
            .then((res) => res.data)
            .then((json) => {
              if(json.isSuccess === "True"){
                alert('회원가입이 완료되었습니다!');
                props.setMode("LOGIN");
              }
              else{
                alert(json.isSuccess);
              }
            });
        }} /></p>
      </div>

      <p>로그인화면으로 돌아가기  <button onClick={() => {
        props.setMode("LOGIN");
      }}>로그인</button></p>
    </>
  );
}

function Board() {
  const [movieContent, setMovieContent] = useState({
    title: '',
    content: ''
  });

  const [viewContent, setViewContent] = useState([]);

  useEffect(() => {
    Axios.get('http://localhost:3001/getContent').then((response) => {
      setViewContent(response.data);
    });
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setMovieContent((prevContent) => ({
      ...prevContent,
      [name]: value
    }));
  };

  const submitContent = () => {
    Axios.post('http://localhost:3001/submitContent', movieContent)
      .then((response) => {
        if (response.data.isSuccess) {
          alert('글 작성이 완료되었습니다!');
          setMovieContent({ title: '', content: '' });
          Axios.get('http://localhost:3001/getContent').then((response) => {
            setViewContent(response.data);
          });
        } else {
          alert('글 작성에 실패했습니다.');
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <>
      <h2>게시판</h2>

      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            name="title"
            placeholder="제목"
            value={movieContent.title}
            onChange={handleInputChange}
          />
        </p>
        <p>
          <CKEditor
            editor={ClassicEditor}
            data={movieContent.content}
            onChange={(event, editor) => {
              const data = editor.getData();
              setMovieContent((prevContent) => ({
                ...prevContent,
                content: data
              }));
            }}
          />
        </p>

        <p>
          <input className="btn" type="submit" value="글 작성" onClick={submitContent} />
        </p>
      </div>

      <div className="content">
        {viewContent.map((item, index) => (
          <div key={index}>
            <h3>{item.title}</h3>
            <p>{ReactHtmlParser(item.content)}</p>
          </div>
        ))}
      </div>
    </>
  );
}

function App() {
  const [mode, setMode] = useState('LOGIN');

  const renderMode = () => {
    if (mode === 'LOGIN') {
      return <Login setMode={setMode} />;
    } else if (mode === 'SIGNIN') {
      return <Signin setMode={setMode} />;
    } else if (mode === 'WELCOME') {
      return <Board />;
    }
  };

  return <div className="App">{renderMode()}</div>;
}

export default App;
