import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import ReactHtmlParser from 'react-html-parser';
import Axios from 'axios';

function Login(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  return (
    <>
      <h2>로그인</h2>

      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            name="username"
            placeholder="아이디"
            onChange={(event) => {
              setId(event.target.value);
            }}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            name="pwd"
            placeholder="비밀번호"
            onChange={(event) => {
              setPassword(event.target.value);
            }}
          />
        </p>

        <p>
          <input
            className="btn"
            type="submit"
            value="로그인"
            onClick={() => {
              const userData = {
                userId: id,
                userPassword: password,
              };
              fetch("http://localhost:3001/login", {
                method: "post",
                headers: {
                  "content-type": "application/json",
                },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {            
                  if (json.isLogin === "True") {
                    props.setMode("WELCOME");
                  } else {
                    alert(json.isLogin);
                  }
                });
            }}
          />
        </p>
      </div>

      <p>
        계정이 없으신가요?{" "}
        <button
          onClick={() => {
            props.setMode("SIGNIN");
          }}
        >
          회원가입
        </button>
      </p>
    </>
  );
}

function Signin(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  return (
    <>
      <h2>회원가입</h2>

      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            placeholder="아이디"
            onChange={(event) => {
              setId(event.target.value);
            }}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호"
            onChange={(event) => {
              setPassword(event.target.value);
            }}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호 확인"
            onChange={(event) => {
              setPassword2(event.target.value);
            }}
          />
        </p>

        <p>
          <input
            className="btn"
            type="submit"
            value="회원가입"
            onClick={() => {
              const userData = {
                userId: id,
                userPassword: password,
                userPassword2: password2,
              };
              fetch("http://localhost:3001/signin", {
                method: "post",
                headers: {
                  "content-type": "application/json",
                },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {
                  alert(json.message);
                  props.setMode("LOGIN");
                });
            }}
          />
        </p>
      </div>

      <p>
        이미 계정이 있으신가요?{" "}
        <button
          onClick={() => {
            props.setMode("LOGIN");
          }}
        >
          로그인
        </button>
      </p>
    </>
  );
}

function Board(props) {
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [fileUrl, setFileUrl] = useState("");
  const [data, setData] = useState([]);

  // 이미지 업로드 처리 함수
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setFile(file);
    setFileUrl(URL.createObjectURL(file));
  };

  // 게시물 작성 처리 함수
  const handleInsert = () => {
    const formData = new FormData();
    formData.append("image", file); // 이미지를 FormData에 추가

    // 이미지 업로드 요청
    Axios.post("http://localhost:3001/api/upload", formData)
      .then((response) => {
        const imageUrl = response.data.imageUrl;

        // 게시물 등록 요청
        Axios.post("http://localhost:3001/api/insert", {
          content: content,
          imageUrl: imageUrl, // 이미지 URL을 서버로 전송
        })
          .then(() => {
            setContent("");
            setFile(null);
            setFileUrl("");
            fetchData();
          })
          .catch((error) => {
            console.error("Error inserting data:", error);
          });
      })
      .catch((error) => {
        console.error("Error uploading image:", error);
      });
  };

  // 게시물 목록 가져오기
  const fetchData = () => {
    Axios.get("http://localhost:3001/api/get")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      <h2>게시판</h2>

      <div className="form">
        <CKEditor
          editor={ClassicEditor}
          data={content}
          onChange={(event, editor) => {
            const data = editor.getData();
            setContent(data);
          }}
        />

        <div>
          <input type="file" onChange={handleFileUpload} />
        </div>

        {fileUrl && (
          <div>
            <img src={fileUrl} alt="Uploaded" />
          </div>
        )}

        <div>
          <button onClick={handleInsert}>등록</button>
        </div>
      </div>

      <div className="board">
        {data.map((item, index) => (
          <div key={index}>
            <h3>{ReactHtmlParser(item.content)}</h3>
            {item.imageUrl && <img src={item.imageUrl} alt="Uploaded" />}
          </div>
        ))}
      </div>
    </>
  );
}

function App() {
  const [mode, setMode] = useState("LOGIN");

  return (
    <div className="App">
      {mode === "LOGIN" && <Login setMode={setMode} />}
      {mode === "SIGNIN" && <Signin setMode={setMode} />}
      {mode === "WELCOME" && <Board />}
    </div>
  );
}

export default App;
