import React, { useState, useEffect } from 'react';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import ReactHtmlParser from 'react-html-parser';
import Axios from 'axios';

function Login(props) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    Axios.post('http://localhost:3001/login', {
      username: username,
      password: password,
    }).then((response) => {
      if (response.data.isLogin === 'True') {
        props.setMode('WELCOME');
      } else {
        alert('로그인에 실패하였습니다.');
      }
    });
  };

  return (
    <>
      <div>
        <h1>로그인</h1>
        <p>
          아이디: <input type='text' onChange={(e) => setUsername(e.target.value)} />
        </p>
        <p>
          비밀번호: <input type='password' onChange={(e) => setPassword(e.target.value)} />
        </p>
        <p>
          <input type='button' value='로그인' onClick={handleLogin} />
        </p>
      </div>

      <p>
        계정이 없으신가요?{' '}
        <button onClick={() => props.setMode('SIGNIN')}>회원가입</button>
      </p>
    </>
  );
}

function Signin(props) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSignin = () => {
    Axios.post('http://localhost:3001/signin', {
      username: username,
      password: password,
    })
      .then((res) => res.json())
      .then((json) => {
        if (json.isSuccess === 'True') {
          alert('회원가입이 완료되었습니다!');
          props.setMode('LOGIN');
        } else {
          alert(json.isSuccess);
        }
      });
  };

  return (
    <>
      <div>
        <h1>회원가입</h1>
        <p>
          아이디: <input type='text' onChange={(e) => setUsername(e.target.value)} />
        </p>
        <p>
          비밀번호: <input type='password' onChange={(e) => setPassword(e.target.value)} />
        </p>
        <p>
          <input type='button' value='가입' onClick={handleSignin} />
        </p>
      </div>

      <p>
        로그인화면으로 돌아가기 <button onClick={() => props.setMode('LOGIN')}>로그인</button>
      </p>
    </>
  );
}

function Board() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [viewContent, setViewContent] = useState([]);

  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
      setViewContent(response.data);
    });
  }, []);

  const handleBoardSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', content);
    formData.append('file', selectedFile);

    await Axios.post('http://localhost:3001/api/insert', formData)
      .then(() => {
        setTitle('');
        setContent('');
        setSelectedFile(null);
        fetchBoardData();
      })
      .catch((err) => console.log(err));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleDelete = async (id) => {
    await Axios.delete(`http://localhost:3001/api/delete/${id}`).then(() => {
      fetchBoardData();
    });
  };

  const fetchBoardData = () => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
      setViewContent(response.data);
    });
  };

  return (
    <>
      <h1>게시판</h1>
      <div>
        <form onSubmit={handleBoardSubmit} encType='multipart/form-data'>
          <input
            className='title-input'
            type='text'
            placeholder='제목'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            name='title'
          />
          <CKEditor
            editor={ClassicEditor}
            data='<p>Hello from CKEditor 5!</p>'
            onChange={(event, editor) => {
              const data = editor.getData();
              setContent(data);
            }}
          />
          <input type='file' onChange={handleFileChange} />
          <button className='submit-button' type='submit'>
            입력
          </button>
        </form>
      </div>

      <div>
        {viewContent.map((val, key) => {
          return (
            <div className='view-content'>
              <h1>{val.title}</h1>
              <div>{ReactHtmlParser(val.content)}</div>
              <img src={`http://localhost:3001/${val.path}`} alt={val.filename} />
              <button onClick={() => handleDelete(val._id)}>삭제</button>
            </div>
          );
        })}
      </div>
    </>
  );
}

function App(props) {
  const [mode, setMode] = useState('');

  useEffect(() => {
    Axios.get('http://localhost:3001/authcheck')
      .then((res) => res.json())
      .then((json) => {
        if (json.isLogin === 'True') {
          setMode('WELCOME');
        } else {
          setMode('LOGIN');
        }
      });
  }, []);

  let content = null;

  if (mode === 'LOGIN') {
    content = <Login setMode={setMode} />;
  } else if (mode === 'SIGNIN') {
    content = <Signin setMode={setMode} />;
  } else if (mode === 'WELCOME') {
    content = (
      <>
        <Board />
        <p>
          <a href='/logout'>로그아웃</a>
        </p>
      </>
    );
  }

  return <>{content}</>;
}

export default App;
